import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";
import ButtonMisson from "../UI/Button";
import Card from "../UI/card";
import CreateTasks from "./createTasks";

interface IFormMission {
  discrption: String;
  missionStatus: String;
  projectId: String;
  data_created: {
    type: Date;
  };
  endDate: { type: Date };
  remarks: String;
}
const TasksList: React.FC<{ onShowMission: IFormMission[] }> = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  const sendingToTheCreation = () => {
    setIsOpen(!isOpen);
  };
  let validtasks =
    props.onShowMission.length === 0 ? (
      <h1>אין משימות בפרוייקט זה</h1>
    ) : (
      <h1>משימות</h1>
    );
  return (
    <div>
      {validtasks}
      <ul>
        {props.onShowMission.map((item: any) => {
          return (
            <Card>
              {`תיאור:${item.discrption}`}
              {`משימות סטטוס:${item.missionStatus}`}
              {`תאריך התחלה${item.data_created}`}
              {`תאריך סיום${item.endDate}`}
              {`הערות${item.remarks}`}
            </Card>
          );
        })}
      </ul>
    </div>
  );
};
export default TasksList;

