import axios from "axios";
import { useState } from "react";
import ButtonMisson from "../UI/Button";
import Card from "../UI/card";
import CreateTasks from "./createTasks";
interface IFormMission {
  discrption: String;
  missionStatus: String;
  projectId: String;
  data_created: {
    type: Date;
  };
  endDate: { type: Date };
  remarks: String;
}
const TasksList: React.FC<{ onShowMission: IFormMission[] }> = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  const sendingToTheCreation = () => {
    setIsOpen(!isOpen);
  };
  let validtasks =
    props.onShowMission.length === 0 ? (
      <h1>אין משימות בפרוייקט זה</h1>
    ) : (
      <h1>משימות</h1>
    );
  return (
    <div>
      {validtasks}
      <ul>
        <Card>
          {props.onShowMission.map((item: any) => {
            return (
              item.discrption,
              item.missionStatus,
              item.projectId,
              item.data_created,
              item.endDate,
              item.remarks
            );
          })}
        </Card>
      </ul>
      {isOpen && <CreateTasks />}
      <ButtonMisson onClick={sendingToTheCreation}> יצירת משימה</ButtonMisson>
    </div>
  );
};
export default TasksList;
