    const [name,setName]=useState('')

    const valueName=(e: React.ChangeEvent<HTMLInputElement>)=>{
         setName(e.target.value)
    }
    const OnHandler=(e: React.ChangeEvent<HTMLInputElement>)=>{
console.log( setName(e.target.value));

    }



return (
    <Card className="AddNewProject">
   
        <form >
  <label>name</label>
  <input type='text' placeholder="name" value={name} onChange={valueName} />
  <br></br>
  <label>decrption</label>
  <input type='text' placeholder="decrption" />
  <br></br>
  <label>team</label>
  <select name="Team" id="sel">
  <option value=''>יש לבחור</option>
    <option value='מתנאל'>מתנאל</option>
    <option value='איתי'>איתי</option>
    <option value='אליהו'>אליהו</option>
    
  </select>
<br/>
 <Button onClick={OnHandler}>Add New Project</Button>
  </form>
    </Card>
  
)


}

export default AddNewProject
