import axios from "axios";
import { useState } from "react";
import ButtonMisson from "../UI/Button";
import Card from "../UI/card";
import CreateTasks from "./createTasks";
interface IFormMission {
  discrption: String;
  missionStatus: String;
  projectId: String;
  data_created: {
    type: Date;
  };
  endDate: { type: Date };
  remarks: String;
}
const TasksList: React.FC<{ onShowMission: IFormMission[] }> = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  const sendingToTheCreation = () => {
    setIsOpen(!isOpen);
  };
  return (
    <div>
      <h1>משימות </h1>
      <Card>{props.onShowMission}</Card>
      {isOpen && <CreateTasks />}
      <ButtonMisson onClick={sendingToTheCreation}> יצירת משימה</ButtonMisson>
    </div>
  );
};
export default TasksList;
