import { Button } from "@mui/material"
import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import ProjectList from "./ProjectList";


const DUMMY=[
    {id:"444",name:"matan",team:"macabi tel aviv"},
    {id:"123",name:"hadad",team:"hapoel jeruzlem"}
]

const AllProjects=()=>{
    const [dataProject,setDataProject]=useState([]);
    const [valideta,setValidate]=useState(false);
  

useEffect(()=>{
    const fatch=async ()=>{
        try {
            let url ='http://localhost:3000/api/routs/router/allProjects'
            let response=await axios.get(url)
            console.log(response.data);
           return  setDataProject(response.data);
        } catch (error) {
            console.log(error);
        }
    }
    fatch()
},[!valideta])
   
useEffect(()=>{
console.log();

},[valideta])
   

return (
 <ProjectList onProps={dataProject}/>
)
}

export default AllProjects
