// import { Button } from "@mui/material";
import classes from "./ProjectList.module.css";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import ButtonMission from "../UI/Button";
// import TasksList from "../tasks/TasksList";
interface ITasks {
  onM: (a: IFormMission[]) => void;
}
interface IFormMission {
  discrption: String;
  missionStatus: String;
  projectId: String;
  data_created: {
    type: Date;
  };
  endDate: { type: Date };
  remarks: String;
}
export interface IProps {
  onM: ITasks;
  _id: string;
  nameProject: string;
  staff: string;
  client: string;
  statusProject: string;
}
const ProjectList: React.FC<{ onProps: IProps[] }> = (props) => {
  const [missionData, SetMissionData] = useState([]);
  const [isOpenMission, setIsOpenMission] = useState(false);
  const [isValideDel, setIsValideDel] = useState(false);
  const [isMasseg, setMessege] = useState<string>("");
  const removeProject = (id: string, a: string) => {
    let url = `http://localhost:3001/api/routs/router/deleteSpcificProject/${id}`;
    axios
      .delete(url)
      .then((response) => {
        console.log(response);
        setIsValideDel(true);
        setMessege(a);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  useEffect(() => {
    if (isValideDel) {
      setMessege("delete");
    }
  }, []);
  const sendProjectID = (id: string) => {
    let url = "http://localhost:3001/api/routs/router/allMissionOfProject";
    axios
      .post(url, { id })
      .then((res) => {
        console.log(res.data);
        SetMissionData(res.data);
        setIsOpenMission(!isOpenMission);
      })
      .catch((res) => {
        console.log(res);
      });
  };

  return (
    <div>
      <ul>
        {props.onProps.map((item, index) => {
          return (
            <div className={classes.specificproject} key={index}>
              <li
                className={classes.allBody}
                onClick={() => {
                  sendProjectID(item._id);
                }}
              >
                {`שם:${item.nameProject}`}
                <br />
                {`מפתח:${item.staff}`}
                <br />
                {`לקוח:${item.client}`}
                <br />
                {`סטטוס:${item.statusProject}`}
                <br />

                <ButtonMission
                  onClick={() => {
                    removeProject(item._id, isMasseg);
                  }}
                >
                  למחיקה לחץ כאן
                </ButtonMission>
                <Link to="/tasks">
                  <ButtonMission>למשימות לחץ כאן</ButtonMission>
                </Link>
              </li>
            </div>
          );
        })}
      </ul>
      <Link to="/createProject">
        <ButtonMission>יצירת פרוייקט</ButtonMission>
      </Link>
    </div>
  );
};

export default ProjectList;
