import React ,{ useState ,useRef,useEffect, FormEvent} from "react"
import Button from "../UI/Button"
import './AddNewProject.css'
import Card from '../UI/card'
import axios from "axios"

export interface IProps{
    project:{
         name:string,
         description:string,
    }
}

const AddNewProject: React.FC =(props) =>{

  
const refName=useRef()
    const [inputNameP,setInputNameP]=useState('')
    const [inputNameStasff,setInputNameStasff]=useState('')
    const [inputNameClent,setInputNameClent]=useState('')
    const [statusP,setStatusP]=useState('')

   const registerPrj=(e: FormEvent<HTMLFormElement>)=>{
e.preventDefault()
   }
return (
    <div>
    

    <Card className="AddNewProject">
    
        <form onSubmit={registerPrj}>
        <h1>יצירת פרוייקט</h1>
  <label>שם פרוייקט</label>
  <input type='text' placeholder="שם פרוייקט" value={inputNameP} />
  <br/>
  <label>שם הצוות </label>
  <input onChange={(e)=>{e.target.value}} type='text' placeholder="שם הצוות" value={inputNameStasff} />
  <br/>
  <label>שם לקוח</label>
  <input type='text' placeholder="שם לקוח" value={inputNameClent} />
 <br/>
  <label>פרוייקט סטטוס</label>
  <select >
  <option>יש לבחור</option>
    <option>פעיל</option>
    <option>תחזוקה</option>
    <option>לא פעיל</option>
  </select>
<br/>
 <Button type="submit">יצירת פרוייקט</Button>
  </form>
    </Card>
    </div>
)


}

export default AddNewProject
