import { Button } from "@mui/material";
import classes from "./ProjectList.module.css";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
export interface IProps {
  id: string;
  nameProject: string;
  staff: string;
  client: string;
  statusProject: string;
}
const ProjectList: React.FC<{ onProps: IProps[] }> = (props) => {
  const [missionData, SetMissionData] = useState([]);

  const ProjectID = async (id: string) => {
    let url = "http://localhost:3001/api/routs/router/allMissionOfProject";
    let response = await axios.post(url, id);
    console.log(response.data);
    SetMissionData(
      response.data.map((item: any) => {
        return item.projectId === id;
      })
    );
  };

  return (
    <ul>
      <div>
        {props.onProps.map((item, index) => {
          return (
            <div className={classes.specificproject} key={index}>
              <li>
                <Button
                  className={classes.allBody}
                  onClick={() => {
                    ProjectID(item.id);
                  }}
                >
                  {`שם:${item.nameProject}`}
                  {`מפתח:${item.staff}`}
                  {`לקוח:${item.client}`}
                  {`סטטוס:${item.statusProject}`}
                </Button>
              </li>
            </div>
          );
        })}
      </div>
    </ul>
  );
};

export default ProjectList;
