import {Route,Switch,Redirect, BrowserRouter}from 'react-router-dom'
function App() {

  return (
  <BrowserRouter>
  <Switch>
    <Route path="/" exact>
    <Redirect to="/projects"/>
  </Route>
  <Route path="/projects" exact>
  <AllProjects />
